package day5;
interface shv{
	void print1();
}
interface hv{
	void print2();
}

interface shvs extends hv{     // If you want to inherit the interface we use extend keyword!!
	void print3();
}
class vishnuu implements shvs{   //// If you want to inherit the interface we use implement keyword!!
	public void print1() {
		System.out.println("hello");
	}
	public void print2() {
		System.out.println("hai");
	}
	public void print3() {
		System.out.println("haiiii");
	}
}

public class InterfaceExtends {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 vishnuu obj=new vishnuu();
 obj.print1();
 obj.print2();
 obj.print3();
	}

}
